        res.send({
          status: false,
          message: "Email doesn't exists!! Enter a valid Email..",
        });
      }
    } else {
      res.send({ status: false, message: "Email Field is Required" });
    }
  } catch (e) {
    res.send({ status: false, messgae: "No Results Found", Result: e });
  }
};


// Reset Password
