import jwt from "jsonwebtoken";

export const verifyToken = async (req, res, next) => {
  try {
    const token = req.header("Authorization");
    let jwtSecretKey = "smartData";

    if (!token) {
      return res.send({
        status: true,
        message: "token is null",
      });
    }
    const decode = jwt.verify(token, jwtSecretKey);
    req.result = decode;
    next();
  } catch (error) {
    console.log(error);
    return res.send({
      status: false,
      message: "Failedd",
      result: error,
    });
  }
};
